(462, 461, '000001_000384_000461_000462', 3, NULL, 0, 'NS-26', 'Transportation of Rails and Sleepers', 0, NULL, 0, NULL, 0, 1),
(463, 462, '000001_000384_000461_000462_000463', 4, NULL, 0, 'NS-26(a)', 'Transportation of rails (supplied by Employer) from point of issue to their final location of laying in track ', 0, NULL, 0, NULL, 0, 1),
(464, 463, '000001_000384_000461_000462_000463_000464', 5, NULL, 0, 'NS-26(a) (i)', 'Average lead, from the place of issue upto the middle point of location of laying in each KM, upto 10 KM ', 0, NULL, 1, NULL, 0, 1),
(465, 463, '000001_000384_000461_000462_000463_000465', 5, NULL, 0, 'NS-26(a)(ii)', 'Incremental rate for lead beyond 10 KM under item NS-26(a) (i) above ', 0, NULL, 1, NULL, 0, 1),
(466, 462, '000001_000384_000461_000462_000466', 4, NULL, 0, 'NS-26(b)', 'Transportation of PSC line sleepers (wider base) from the place of issue  (in case of sleepers issued free of cost by Employer)  to  their final location of laying.\nNote:\n(1)100% payment shall be made on transportation to designated site as directed by employer/engineer.\n(2)  If the sleepers to be issued free of cost by the employer are made available at the sleeper plant and loading of sleepers is done by concrete sleeper plant then the rate quoted in the item shall be applicable subject to recovery @ Rs.10/- per sleeper.\n(3) Rate includes multiple handlings. Nothing extra shall be paid for multiple handlings\n\n', 0, NULL, 0, NULL, 0, 1),
